def extract_the_last_year(df, column_name):
    df_copy = df.copy()

    def extract_year(date_str):
        year = int(date_str[:4])
        month = int(date_str[5:])

        if month == 12:
            year += 1

        if month == 0:
            year -= 1

        return year

    df_copy[column_name] = df_copy[column_name].apply(extract_year)

    return df_copy[column_name]

