def drop2rows_zero(df):
    df_copy = df.copy()
    for col in df_copy.columns:
        zero_indices = df_copy.index[df_copy[col] == 0].tolist()
        for idx in zero_indices:
            if idx % 2 == 1:
                df_copy = df_copy.drop([idx-1, idx])
    return df_copy

